tables.table_schema:
CREATE TABLE CharityDonations (
	donation_id INT PRIMARY KEY,
	charity_name VARCHAR,
	donation_amount DECIMAL,
	donation_date DATE
);

CREATE TABLE philanthropy_donors (
    donor_id INT PRIMARY KEY,
    donor_name VARCHAR(50),
    donation_amount DECIMAL,
    donation_date DATE
);

CREATE TABLE philanthropy_organizations (
    org_id INT PRIMARY KEY,
    org_name VARCHAR(50),
    sector VARCHAR(50),
    founding_date DATE
);
