tables.table_schema:
CREATE TABLE grant_applications (
    application_id SERIAL PRIMARY KEY,
    applicant_name VARCHAR(100),
    grant_amount DECIMAL,
    application_date DATE
);

CREATE TABLE grant_analysis (
    analysis_id SERIAL PRIMARY KEY,
    application_id INTEGER REFERENCES grant_applications(application_id),
    success_rate DECIMAL,
    analysis_date DATE
);

CREATE TABLE grant_applications (
  application_id UUID PRIMARY KEY,
  applicant_name TEXT,
  grant_amount DECIMAL,
  application_date DATE
);

CREATE TABLE grant_reviewers (
  reviewer_id UUID PRIMARY KEY,
  reviewer_name TEXT,
  expertise_area TEXT
);

CREATE TABLE grant_analysis (
  analysis_id UUID PRIMARY KEY,
  review_id UUID,
  application_id UUID,
  success BOOLEAN
);

CREATE TABLE ApplicationHistory (
    application_id UUID,
    history_date DATE,
    status VARCHAR(50)
);

CREATE TABLE Reviewers (
    reviewer_id UUID PRIMARY KEY,
    reviewer_name VARCHAR(255),
    affiliation VARCHAR(100)
);

CREATE TABLE ReviewScores (
    review_id UUID,
    reviewer_id UUID,
    criteria VARCHAR(255),
    score DECIMAL(5,2)
);

CREATE TABLE GrantTypes (
    grant_type_id UUID PRIMARY KEY,
    grant_type_name VARCHAR(100),
    category VARCHAR(50)
);
