tables.table_schema:
CREATE TABLE budget_info (
  grant_id INT PRIMARY KEY,
  proposal_title TEXT,
  budget_amount DECIMAL
);

CREATE TABLE GrantProposals (
  grant_id INT PRIMARY KEY,
  title VARCHAR(255),
  amount DECIMAL,
  submit_date DATE
);

CREATE TABLE BudgetBreakdown (
  item_id INT PRIMARY KEY,
  grant_id INT,
  item_name VARCHAR(255),
  item_amount DECIMAL
);

CREATE TABLE ProposalComments (
  comment_id INT PRIMARY KEY,
  grant_id INT,
  commenter_name VARCHAR(255),
  comment_text TEXT
);

CREATE TABLE grants (
  grant_id INT PRIMARY KEY,
  grant_title VARCHAR(255),
  grant_amount DECIMAL,
  grant_deadline DATE
);

CREATE TABLE organizations (
  org_id INT PRIMARY KEY,
  org_name VARCHAR(255),
  org_address VARCHAR(255),
  org_contact_person VARCHAR(255)
);

CREATE TABLE budgets (
  budget_id INT PRIMARY KEY,
  grant_id INT,
  budget_amount DECIMAL,
  budget_category VARCHAR(255)
);

CREATE TABLE expenses (
  expense_id INT PRIMARY KEY,
  budget_id INT,
  expense_amount DECIMAL,
  expense_description TEXT
);

CREATE TABLE transactions (
  transaction_id INT PRIMARY KEY,
  expense_id INT,
  transaction_date DATE,
  transaction_amount DECIMAL
);
