tables.table_schema:
CREATE TABLE grant_evaluation_criteria (
  criterion_id UUID PRIMARY KEY,
  criterion_name VARCHAR(255) NOT NULL,
  description TEXT,
  weightage DECIMAL(5,2)
);

CREATE TABLE scoring_rubrics (
  rubric_id UUID PRIMARY KEY,
  rubric_name VARCHAR(255) NOT NULL,
  criterion_id UUID,
  score_range JSONB
);

CREATE TABLE evaluation_criteria (
 criterion_id INT PRIMARY KEY,
 criterion_name VARCHAR(50) NOT NULL,
 description TEXT
);

CREATE TABLE grant_evaluation_criteria (
  id INT PRIMARY KEY,
  category VARCHAR(255),
  description TEXT
);

CREATE TABLE scoring_rubrics (
  id INT PRIMARY KEY,
  criteria_id INT,
  score INT,
  feedback TEXT
);

CREATE TABLE evaluation_scores (
  id INT PRIMARY KEY,
  user_id INT,
  criteria_id INT,
  score INT
);

CREATE TABLE decision_making_process (
  id INT PRIMARY KEY,
  evaluation_id INT,
  decision VARCHAR(255)
);

CREATE TABLE grant_criteria (
    criteria_id INT PRIMARY KEY,
    criteria_name VARCHAR(255),
    description TEXT
);

CREATE TABLE rubrics (
    rubric_id INT PRIMARY KEY,
    rubric_name VARCHAR(255),
    criteria_id INT,
    FOREIGN KEY (criteria_id) REFERENCES grant_criteria(criteria_id)
);

CREATE TABLE evaluation_scores (
    score_id INT PRIMARY KEY,
    rubric_id INT,
    evaluator_id INT,
    score INT,
    FOREIGN KEY (rubric_id) REFERENCES rubrics(rubric_id)
);

CREATE TABLE evaluators (
    evaluator_id INT PRIMARY KEY,
    evaluator_name VARCHAR(255),
    department VARCHAR(255)
);

CREATE TABLE grant_applications (
    application_id INT PRIMARY KEY,
    applicant_name VARCHAR(255),
    grant_amount DECIMAL(10, 2),
    application_date DATE
);
