tables.table_schema:
CREATE TABLE grants (
  grant_id INT PRIMARY KEY,
  title VARCHAR(255),
  agency VARCHAR(100),
  deadline DATE
);

CREATE TABLE collaborators (
  collaborator_id INT PRIMARY KEY,
  name VARCHAR(100),
  email VARCHAR(255),
  role VARCHAR(50)
);

CREATE TABLE grant_writers (
	id INT PRIMARY KEY,
	name VARCHAR(255) NOT NULL,
	email VARCHAR(255) NOT NULL,
	role VARCHAR(50)
);

CREATE TABLE grant_projects (
	project_id INT PRIMARY KEY,
	project_name VARCHAR(255) NOT NULL,
	deadline DATE,
	status VARCHAR(50)
);

CREATE TABLE grant_writers (
    id SERIAL PRIMARY KEY,
    full_name TEXT NOT NULL,
    email TEXT NOT NULL,
    role TEXT
);

CREATE TABLE grant_writers (
  writer_id INT PRIMARY KEY,
  writer_name VARCHAR(255) NOT NULL,
  writer_email VARCHAR(255) NOT NULL,
  writer_department VARCHAR(255) NOT NULL
);

CREATE TABLE grant_projects (
  project_id INT PRIMARY KEY,
  project_title VARCHAR(255) NOT NULL,
  project_description TEXT,
  project_status VARCHAR(50) NOT NULL
);
