tables.table_schema:
CREATE TABLE supporters (
 supporter_id INT PRIMARY KEY,
 supporter_name VARCHAR(50),
 email VARCHAR(100),
 phone_number VARCHAR(20)
);

CREATE TABLE engagement_levels (
 level_id INT PRIMARY KEY,
 level_name VARCHAR(50),
 description VARCHAR(200)
);

CREATE TABLE CampaignDonations (
 donation_id UUID PRIMARY KEY,
 supporter_id UUID REFERENCES CampaignSupporters(supporter_id),
 donation_date TIMESTAMP NOT NULL,
 amount DECIMAL(10, 2) NOT NULL
);
