tables.table_schema:
CREATE TABLE past_campaigns (
  campaign_id UUID PRIMARY KEY,
  campaign_name TEXT,
  start_date DATE,
  end_date DATE
);

CREATE TABLE campaign_participants (
  participant_id UUID PRIMARY KEY,
  campaign_id UUID REFERENCES past_campaigns(campaign_id),
  participant_name TEXT,
  participation_date DATE
);

CREATE TABLE campaign_metrics (
  metric_id UUID PRIMARY KEY,
  campaign_id UUID REFERENCES past_campaigns(campaign_id),
  metric_name TEXT,
  metric_value FLOAT
);

CREATE TABLE advocacy_campaigns (
    campaign_id UUID PRIMARY KEY,
    campaign_name VARCHAR(255) NOT NULL,
    campaign_date DATE NOT NULL,
    total_donations DECIMAL(10,2) DEFAULT 0
);

CREATE TABLE campaign_performance (
    performance_id SERIAL PRIMARY KEY,
    campaign_id UUID REFERENCES advocacy_campaigns(campaign_id),
    performance_metric VARCHAR(50) NOT NULL,
    metric_value DECIMAL(10,2) NOT NULL
);

CREATE TABLE campaign_participants (
    participant_id SERIAL PRIMARY KEY,
    campaign_id UUID REFERENCES advocacy_campaigns(campaign_id),
    participant_name VARCHAR(100) NOT NULL,
    participant_email VARCHAR(255) NOT NULL
);

CREATE TABLE campaign_feedback (
    feedback_id SERIAL PRIMARY KEY,
    campaign_id UUID REFERENCES advocacy_campaigns(campaign_id),
    feedback_text TEXT NOT NULL,
    feedback_date TIMESTAMP NOT NULL
);
