tables.table_schema:
CREATE TABLE legislators (
  legislator_id INT PRIMARY KEY,
  legislator_name STRING,
  contact_info STRING
);

CREATE TABLE voting_records (
  record_id INT PRIMARY KEY,
  legislator_id INT,
  vote_date DATE,
  vote_result STRING
);

CREATE TABLE legislators (
	nicename STRING PRIMARY KEY,
	party STRING,
	state STRING
);

CREATE TABLE contacts (
	email STRING PRIMARY KEY,
	phone STRING,
	address STRING,
	legislator_nicename STRING REFERENCES legislators(nicename)
);

CREATE TABLE voting_records (
	record_id UUID PRIMARY KEY,
	legislator_nicename STRING REFERENCES legislators(nicename),
	political_party STRING,
	vote_decision STRING
);

CREATE TABLE voting_records (
  record_id INT PRIMARY KEY,
  legislator_id INT REFERENCES legislators(id),
  vote_decision TEXT,
  vote_date DATE
);

CREATE TABLE advocacy_groups (
  group_id INT PRIMARY KEY,
  group_name TEXT,
  group_description TEXT
);

CREATE TABLE group_legislators (
  group_id INT REFERENCES advocacy_groups(group_id),
  legislator_id INT REFERENCES legislators(id),
  join_date DATE
);

CREATE TABLE advocacy_targets (
  target_id UUID PRIMARY KEY,
  target_name VARCHAR(100) NOT NULL,
  target_description TEXT,
  issue_area VARCHAR(50)
);
