tables.table_schema:
CREATE TABLE advocacy_initiatives (
  id SERIAL PRIMARY KEY,
  initiative_name TEXT NOT NULL,
  start_date DATE NOT NULL,
  deadline DATE NOT NULL
);

CREATE TABLE milestones (
  id SERIAL PRIMARY KEY,
  milestone_name TEXT NOT NULL,
  milestone_date DATE NOT NULL,
  completed BOOLEAN NOT NULL
);

CREATE TABLE advocacy_initiatives (
    initiative_id UUID PRIMARY KEY,
    initiative_name VARCHAR(100),
    start_date DATE,
    end_date DATE
);

CREATE TABLE milestones (
    milestone_id UUID PRIMARY KEY,
    milestone_description TEXT,
    milestone_date DATE,
    initiative_id UUID REFERENCES advocacy_initiatives (initiative_id)
);

CREATE TABLE deadlines (
    deadline_id UUID PRIMARY KEY,
    deadline_description TEXT,
    deadline_date DATE,
    initiative_id UUID REFERENCES advocacy_initiatives (initiative_id)
);

CREATE TABLE action_items (
    action_item_id UUID PRIMARY KEY,
    action_item_description TEXT,
    status VARCHAR(50),
    deadline_id UUID REFERENCES deadlines (deadline_id)
);

CREATE TABLE Users (
    id INT PRIMARY KEY,
    username VARCHAR(100),
    email VARCHAR(255),
    role VARCHAR(50)
);
