tables.table_schema:
CREATE TABLE campaign_participants (
    participant_id SERIAL PRIMARY KEY,
    participant_name VARCHAR(255) NOT NULL,
    campaign_id INTEGER REFERENCES advocacy_campaigns(campaign_id) ON DELETE CASCADE
);

CREATE TABLE policy_changes (
    policy_id UUID PRIMARY KEY,
    policy_name TEXT,
    impact_score INTEGER
);

CREATE TABLE public_awareness (
    awareness_id UUID PRIMARY KEY,
    campaign_name TEXT,
    awareness_level INTEGER,
    reach INTEGER
);

CREATE TABLE community_engagement (
    engagement_id UUID PRIMARY KEY,
    event_name TEXT,
    participants INTEGER
);

CREATE TABLE policy_changes (
    policy_id INT PRIMARY KEY,
    policy_name VARCHAR(255) NOT NULL,
    policy_description TEXT,
    date_enacted DATE
);

CREATE TABLE public_awareness (
    awareness_id INT PRIMARY KEY,
    awareness_topic VARCHAR(255) NOT NULL,
    awareness_description TEXT,
    awareness_date DATE
);

CREATE TABLE community_engagement (
    engagement_id INT PRIMARY KEY,
    engagement_type VARCHAR(50) NOT NULL,
    engagement_description TEXT,
    engagement_date DATE
);
