tables.table_schema:
CREATE TABLE sustainability_report (
    company_id INT,
    social_impact DECIMAL,
    environmental_impact DECIMAL
);

CREATE TABLE company_impact (
    company_id INT PRIMARY KEY,
    social_impact_score FLOAT,
    environmental_impact_score FLOAT
);

CREATE TABLE company ( 
   company_id INT PRIMARY KEY, 
   company_name VARCHAR(255), 
   company_location VARCHAR(255) 
 );

CREATE TABLE social_impact ( 
   impact_id INT PRIMARY KEY, 
   company_id INT, 
   category VARCHAR(50), 
   impact_description TEXT 
 );

CREATE TABLE environmental_impact ( 
   impact_id INT PRIMARY KEY, 
   company_id INT, 
   category VARCHAR(50), 
   impact_description TEXT 
 );

CREATE TABLE EnergyConsumption (
    id UUID PRIMARY KEY,
    company_name VARCHAR,
    electricity_consumption DECIMAL,
    gas_consumption DECIMAL
);

CREATE TABLE SocialImpact (
    id UUID PRIMARY KEY,
    company_name VARCHAR,
    employee_diversity VARCHAR,
    community_engagement VARCHAR
);

CREATE TABLE ReportingPeriod (
    id UUID PRIMARY KEY,
    company_name VARCHAR,
    start_date DATE,
    end_date DATE
);

CREATE TABLE product(
    product_id INT PRIMARY KEY,
    product_name VARCHAR(255),
    category VARCHAR(255),
    price DECIMAL
);

CREATE TABLE sustainability_goal(
    goal_id INT PRIMARY KEY,
    goal_description VARCHAR(500),
    target_date DATE,
    progress DECIMAL
);
