tables.table_schema:
CREATE TABLE csr_goals (
  goal_id INT PRIMARY KEY,
  goal_name VARCHAR(255) NOT NULL,
  target_date DATE NOT NULL,
  progress DECIMAL(5,2) DEFAULT 0.00
);

CREATE TABLE csr_goals (
    goal_id INT PRIMARY KEY,
    goal_name VARCHAR(50) NOT NULL,
    target_value DECIMAL(10, 2) NOT NULL,
    progress_value DECIMAL(10, 2) DEFAULT 0
);

CREATE TABLE csr_metrics (
    id INT PRIMARY KEY,
    metric_name VARCHAR(255),
    target_value INT
);

CREATE TABLE csr_projects (
    id INT PRIMARY KEY,
    project_name VARCHAR(255),
    start_date DATE,
    end_date DATE
);

CREATE TABLE csr_teams (
    id INT PRIMARY KEY,
    team_name VARCHAR(255),
    team_lead VARCHAR(255)
);

CREATE TABLE csr_reports (
    id INT PRIMARY KEY,
    report_name VARCHAR(255),
    report_date DATE,
    report_content TEXT
);
