tables.table_schema:
CREATE TABLE player_profiles (
    id UUID PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    stats JSONB,
    achievements JSONB
);

CREATE TABLE player_profiles (
    id INT PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    stats JSONB,
    achievements JSONB
);

CREATE TABLE player_profiles (
    player_id INT PRIMARY KEY,
    username VARCHAR(50) UNIQUE,
    email VARCHAR(100),
    join_date TIMESTAMP
);

CREATE TABLE player_achievements (
    achievement_id INT PRIMARY KEY,
    player_id INT REFERENCES player_profiles(player_id),
    achievement_name VARCHAR(100),
    date_achieved DATE
);

CREATE TABLE player_friends (
    friendship_id INT PRIMARY KEY,
    player_id1 INT REFERENCES player_profiles(player_id),
    player_id2 INT REFERENCES player_profiles(player_id),
    friend_since TIMESTAMP
);

