tables.table_schema:
CREATE TABLE game_settings (
  player_id UUID PRIMARY KEY,
  sound_volume INT,
  music_volume INT,
  difficulty_level TEXT
);

CREATE TABLE game_settings (
    player_id INT PRIMARY KEY,
    theme VARCHAR(50),
    sound_volume INT,
    difficulty_level VARCHAR(20)
);

CREATE TABLE player_preferences (
    player_id INT PRIMARY KEY,
    display_name VARCHAR(100),
    language VARCHAR(50),
    timezone VARCHAR(50)
);

CREATE TABLE game_settings (
  player_id INT PRIMARY KEY,
  volume FLOAT,
  difficulty INT
);

CREATE TABLE player_preferences (
  preference_id INT PRIMARY KEY,
  player_id INT REFERENCES game_settings(player_id),
  theme VARCHAR(50),
  controls VARCHAR(50)
);

CREATE TABLE player_inventory (
  inventory_id INT PRIMARY KEY,
  player_id INT REFERENCES game_settings(player_id),
  item_name VARCHAR(50),
  quantity INT
);

CREATE TABLE game_settings (
    player_id INT PRIMARY KEY,
    graphics_quality VARCHAR(255),
    sound_volume INT,
    controls_config VARCHAR(255)
);

CREATE TABLE player_preferences (
    player_id INT PRIMARY KEY,
    language_preference VARCHAR(255),
    theme_preference VARCHAR(255),
    notification_settings BOOLEAN
);

CREATE TABLE achievements (
    achievement_id INT PRIMARY KEY,
    player_id INT,
    achievement_name VARCHAR(255),
    achievement_description TEXT
);

CREATE TABLE levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(255),
    difficulty_level VARCHAR(50),
    score_requirement INT
);

CREATE TABLE PlayerPreferences (
  player_id UUID PRIMARY KEY,
  volume_level FLOAT,
  music_enabled BOOLEAN
);

CREATE TABLE GameSettings (
  game_id UUID PRIMARY KEY,
  difficulty_level VARCHAR,
  tutorial_enabled BOOLEAN
);

CREATE TABLE GraphicsSettings (
  graphics_id UUID PRIMARY KEY,
  resolution VARCHAR,
  graphics_quality VARCHAR
);

CREATE TABLE ControlSettings (
  control_id UUID PRIMARY KEY,
  sensitivity FLOAT,
  invert_y_axis BOOLEAN
);

CREATE TABLE CustomizationSettings (
  customization_id UUID PRIMARY KEY,
  theme VARCHAR,
  character_skin VARCHAR
);
