tables.table_schema:
CREATE TABLE game_maps (
    map_id UUID PRIMARY KEY,
    map_name VARCHAR(100),
    level_design JSONB
);

CREATE TABLE game_maps (
    map_id UUID PRIMARY KEY,
    map_name VARCHAR(255),
    map_data JSONB
);

CREATE TABLE level_designs (
    design_id UUID PRIMARY KEY,
    map_id UUID REFERENCES game_maps(map_id),
    level_number INT,
    design_data JSONB
);

CREATE TABLE game_maps (
    map_id INT PRIMARY KEY,
    map_name VARCHAR(100),
    map_data JSON
);

CREATE TABLE maps (
    map_id INT PRIMARY KEY,
    map_name VARCHAR(255) NOT NULL,
    map_data JSONB NOT NULL
);

CREATE TABLE levels (
    level_id INT PRIMARY KEY,
    level_name VARCHAR(255) NOT NULL,
    map_id INT REFERENCES maps(map_id) NOT NULL
);

CREATE TABLE obstacles (
    obstacle_id INT PRIMARY KEY,
    obstacle_name VARCHAR(255) NOT NULL,
    level_id INT REFERENCES levels(level_id) NOT NULL
);

CREATE TABLE checkpoints (
    checkpoint_id INT PRIMARY KEY,
    checkpoint_name VARCHAR(255) NOT NULL,
    level_id INT REFERENCES levels(level_id) NOT NULL
);

CREATE TABLE game_maps (
    map_id INT PRIMARY KEY,
    map_name TEXT NOT NULL,
    map_data BYTEA
);

CREATE TABLE level_designs (
    level_id INT PRIMARY KEY,
    map_id INT REFERENCES game_maps(map_id),
    level_name TEXT NOT NULL,
    level_data BYTEA
);

CREATE TABLE gameplay_progress (
    player_id INT,
    level_id INT REFERENCES level_designs(level_id),
    progress_percentage FLOAT,
    PRIMARY KEY (player_id, level_id)
);

CREATE TABLE player_stats (
    player_id INT PRIMARY KEY,
    player_name TEXT NOT NULL,
    total_score INT
);

CREATE TABLE player_inventory (
    player_id INT REFERENCES player_stats(player_id),
    item_id INT PRIMARY KEY,
    item_name TEXT NOT NULL,
    quantity INT
);
