tables.table_schema:
CREATE TABLE reviews (
  review_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  user_id INT,
  rating FLOAT,
  review_text TEXT
);

CREATE TABLE products (
  product_id INT PRIMARY KEY,
  product_name VARCHAR(255),
  category VARCHAR(50),
  brand VARCHAR(50),
  price DECIMAL(10,2)
);

CREATE TABLE users (
  user_id INT PRIMARY KEY,
  username VARCHAR(50),
  email VARCHAR(100),
  join_date DATE
);

CREATE TABLE accessories (
  accessory_id INT PRIMARY KEY,
  accessory_name VARCHAR(255),
  accessory_type VARCHAR(50),
  price DECIMAL(10, 2)
);

