tables.table_schema:
CREATE TABLE warranty_information (
    id INT PRIMARY KEY,
    accessory_name VARCHAR(50),
    warranty_start_date DATE,
    warranty_end_date DATE
);

CREATE TABLE accessories (
    id INT PRIMARY KEY,
    accessory_name VARCHAR(255),
    warranty_start DATE,
    warranty_end DATE
);

CREATE TABLE service_history (
    service_id INT PRIMARY KEY,
    accessory_id INT,
    service_date TIMESTAMP,
    service_details TEXT
);

CREATE TABLE warranty_info (
    warranty_id INT PRIMARY KEY,
    accessory_id INT,
    warranty_provider VARCHAR(255),
    warranty_length INT
);

CREATE TABLE accessories (
    accessory_id INT PRIMARY KEY,
    product_name TEXT,
    warranty_start_date DATE,
    warranty_end_date DATE
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    address TEXT,
    phone_number TEXT
);

CREATE TABLE ServiceHistory (
	service_id INT PRIMARY KEY,
	accessory_id INT,
	service_date DATE,
	description TEXT
);

CREATE TABLE Customer (
	customer_id INT PRIMARY KEY,
	name VARCHAR(50),
	email VARCHAR(50),
	phone_number VARCHAR(20)
);

CREATE TABLE AccessoryCustomer (
	customer_id INT,
	accessory_id INT,
	purchase_date DATE,
	PRIMARY KEY (customer_id, accessory_id)
);
