tables.table_schema:
CREATE TABLE gaming_accessories_pricing (
    id INT PRIMARY KEY,
    accessory_name VARCHAR(255),
    price DECIMAL,
    discount DECIMAL
);

CREATE TABLE gaming_accessories (
  accessory_id INT PRIMARY KEY,
  accessory_name VARCHAR(50),
  price DECIMAL(10, 2),
  discount DECIMAL(5, 2)
);

CREATE TABLE accessory_categories (
  category_id INT PRIMARY KEY,
  category_name VARCHAR(50),
  accessory_id INT,
  FOREIGN KEY (accessory_id) REFERENCES gaming_accessories(accessory_id)
);

CREATE TABLE gaming_accessories (
  accessory_id SERIAL PRIMARY KEY,
  accessory_name VARCHAR(255) NOT NULL,
  accessory_type VARCHAR(50) NOT NULL,
  price DECIMAL(8, 2) NOT NULL
);

CREATE TABLE discount_codes (
  discount_id SERIAL PRIMARY KEY,
  code VARCHAR(10) UNIQUE NOT NULL,
  discount_amount DECIMAL(4, 2) NOT NULL,
  active BOOLEAN DEFAULT TRUE
);

CREATE TABLE accessory_inventory (
  inventory_id SERIAL PRIMARY KEY,
  accessory_id INT REFERENCES gaming_accessories(accessory_id),
  quantity INT NOT NULL,
  location VARCHAR(100) NOT NULL
);

CREATE TABLE categories (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50)
);

CREATE TABLE products (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100),
    category_id INT,
    price DECIMAL(10, 2)
);

CREATE TABLE accessories (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    price NUMERIC(10,2) NOT NULL,
    discount_percent DECIMAL(4,2) NOT NULL
);

CREATE TABLE categories (
    id SERIAL PRIMARY KEY,
    name VARCHAR(50) NOT NULL
);

CREATE TABLE customers (
    id SERIAL PRIMARY KEY,
    name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL
);
