tables.table_schema:
CREATE TABLE communication_logs (
    log_id SERIAL PRIMARY KEY,
    team_id INT,
    channel_id INT,
    message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE chat_history (
    chat_id SERIAL PRIMARY KEY,
    player_id INT,
    message TEXT,
    timestamp TIMESTAMP
);

CREATE TABLE team_members (
    member_id SERIAL PRIMARY KEY,
    team_id INT,
    player_id INT,
    role VARCHAR(50)
);

CREATE TABLE team (team_id INT PRIMARY KEY, team_name VARCHAR(50) NOT NULL, region VARCHAR(50) NOT NULL);

CREATE TABLE player (player_id INT PRIMARY KEY, player_name VARCHAR(50) NOT NULL, team_id INT NOT NULL, FOREIGN KEY (team_id) REFERENCES team(team_id));

CREATE TABLE communication_channel (channel_id INT PRIMARY KEY, channel_name VARCHAR(50) NOT NULL);

CREATE TABLE communication_log (log_id INT PRIMARY KEY, player_id INT NOT NULL, channel_id INT NOT NULL, log_message TEXT NOT NULL, log_timestamp TIMESTAMP NOT NULL, FOREIGN KEY (player_id) REFERENCES player(player_id), FOREIGN KEY (channel_id) REFERENCES communication_channel(channel_id));
