tables.table_schema:
CREATE TABLE TransferHistory (
 player_id INT,
 previous_team VARCHAR,
 new_team VARCHAR,
 transfer_date DATE
);

CREATE TABLE TeamDetails (
 team_id INT,
 team_name VARCHAR,
 country VARCHAR
);

CREATE TABLE PlayerTransferHistory (
  player_id INT,
  from_team_id INT,
  to_team_id INT,
  transfer_date DATE
);

CREATE TABLE Team (
  team_id INT,
  team_name VARCHAR(50),
  country VARCHAR(50)
);

CREATE TABLE EsportsLeague (
  league_id INT,
  league_name VARCHAR(50),
  prize_pool DECIMAL(10, 2)
);

CREATE TABLE Player (
   player_id INT PRIMARY KEY,
   player_name VARCHAR(50) NOT NULL,
   team_id INT,
   date_of_birth DATE
);

CREATE TABLE Team (
   team_id INT PRIMARY KEY,
   team_name VARCHAR(50) NOT NULL,
   country VARCHAR(50) NOT NULL
);

CREATE TABLE TransferHistory (
   transfer_id INT PRIMARY KEY,
   player_id INT,
   from_team_id INT,
   to_team_id INT,
   transfer_date DATE
);

CREATE TABLE TradeRecords (
   trade_id INT PRIMARY KEY,
   player_id INT,
   team_id INT,
   trade_date DATE
);

CREATE TABLE Tournament (
   tournament_id INT PRIMARY KEY,
   tournament_name VARCHAR(50) NOT NULL,
   game_name VARCHAR(50) NOT NULL
);
