tables.table_schema:
CREATE TABLE game_leaderboard (
    leaderboard_id INT,
    game_name VARCHAR(255),
    player_id INT,
    score INT
);

CREATE TABLE PlayerStatistics (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    game_id INT,
    score INT
);

CREATE TABLE Game (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(50),
    release_date DATE
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(255),
    player_score INT
);

CREATE TABLE games (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(255),
    release_date DATE
);

CREATE TABLE scores (
    score_id INT PRIMARY KEY,
    player_id INT,
    game_id INT,
    score_value INT
);

CREATE TABLE leaderboard (
    leaderboard_id INT PRIMARY KEY,
    game_id INT,
    player_id INT,
    rank INT
);
