  event_id UUID PRIMARY KEY,
  event_name STRING,
  event_description STRING,
  event_date DATE
);

CREATE TABLE Quests (
  quest_id UUID PRIMARY KEY,
  quest_name STRING,
  quest_description STRING,
  quest_difficulty INTEGER
);

CREATE TABLE Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255),
    event_description TEXT,
    event_date DATE
);

CREATE TABLE Quests (
    quest_id INT PRIMARY KEY,
    quest_name VARCHAR(255),
    quest_description TEXT,
    quest_difficulty INT
);

CREATE TABLE events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(255),
    event_date DATE,
    event_description TEXT
);

CREATE TABLE players (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(255),
    player_level INT,
    player_experience INT
);

CREATE TABLE PlayerQuestProgress (
  player_id INT REFERENCES Players(player_id),
  quest_id INT REFERENCES Quests(quest_id),
  progress_percentage INT DEFAULT 0,
  PRIMARY KEY (player_id, quest_id)
);

CREATE TABLE EventRegistrations (
  event_id INT REFERENCES Events(event_id),
  player_id INT REFERENCES Players(player_id),
  registration_date TIMESTAMP NOT NULL,
  PRIMARY KEY (event_id, player_id)
);
