tables.table_schema:
CREATE TABLE game_data (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(255),
    genre VARCHAR(50),
    release_date DATE
);

CREATE TABLE LocalizationData (
    id INT PRIMARY KEY,
    key VARCHAR(255),
    value TEXT,
    language VARCHAR(50)
);

CREATE TABLE Game (
    game_id INT PRIMARY KEY,
    title VARCHAR(255),
    genre VARCHAR(50),
    release_date DATE
);

CREATE TABLE LocalizationKeys (
    key_id INT PRIMARY KEY,
    key_name VARCHAR(255),
    description TEXT,
    category VARCHAR(50)
);

CREATE TABLE Languages (
    language_id INT PRIMARY KEY,
    language_name VARCHAR(50),
    region VARCHAR(50),
    supported BOOLEAN
);
