tables.table_schema:
CREATE TABLE player_feedback (
    feedback_id INT PRIMARY KEY,
    game_id INT,
    player_name VARCHAR(50),
    feedback_text TEXT
);

CREATE TABLE indie_games (
    game_id INT PRIMARY KEY,
    game_name VARCHAR(50),
    genre VARCHAR(30),
    release_year INT
);

CREATE TABLE player_feedback (
    player_id INT,
    game_id INT,
    feedback TEXT
);

CREATE TABLE game_reviews (
    review_id INT,
    game_id INT,
    review_text TEXT,
    rating INT
);

CREATE TABLE players (
    player_id INT,
    username VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE games (
    game_id INT,
    game_title VARCHAR(100),
    developer VARCHAR(50),
    release_date DATE
);

CREATE TABLE developers (
    developer_id INT,
    developer_name VARCHAR(50),
    country VARCHAR(50)
);
