tables.table_schema:
CREATE TABLE player_details (
    player_id INT PRIMARY KEY,
    player_name VARCHAR,
    player_level INT,
    join_date DATE
);

CREATE TABLE PlayerInfo (
    player_id INT PRIMARY KEY,
    player_name VARCHAR(50),
    player_level INT,
    join_date DATE,
);

CREATE TABLE GameEvents (
    event_id INT PRIMARY KEY,
    session_id INT,
    event_type VARCHAR(50),
    event_timestamp TIMESTAMP,
);
