tables.table_schema:
CREATE TABLE player_demographics (
    player_id UUID PRIMARY KEY,
    age INT,
    gender VARCHAR,
    location VARCHAR
);

CREATE TABLE game_catalog (
    game_id INT PRIMARY KEY,
    game_title VARCHAR,
    game_category VARCHAR,
    release_year INT
);

CREATE TABLE games (
    game_id INT PRIMARY KEY,
    game_title STRING,
    genre STRING,
    release_year INT
);

CREATE TABLE player_statistics (
    stats_id INT PRIMARY KEY,
    player_id INT REFERENCES players(player_id),
    total_playtime INT,
    favorite_genre STRING
);
