tables.table_schema:
CREATE TABLE fashion_designers (
    designer_id SERIAL PRIMARY KEY,
    designer_name VARCHAR(255) NOT NULL,
    email VARCHAR(255) UNIQUE
);

CREATE TABLE design_sketches (
    sketch_id SERIAL PRIMARY KEY,
    designer_id INT NOT NULL,
    sketch_description TEXT,
    sketch_image BYTEA
);

CREATE TABLE fabric_samples (
    sample_id SERIAL PRIMARY KEY,
    designer_id INT NOT NULL,
    fabric_name VARCHAR(255),
    fabric_image BYTEA
);

CREATE TABLE design_sketches (
    sketch_id UUID PRIMARY KEY,
    designer_id UUID,
    sketch_data BYTEA,
    FOREIGN KEY (designer_id) REFERENCES designers(designer_id)
);

CREATE TABLE fabric_samples (
    sample_id UUID PRIMARY KEY,
    designer_id UUID,
    sample_data BYTEA,
    FOREIGN KEY (designer_id) REFERENCES designers(designer_id)
);

CREATE TABLE production_notes (
    note_id UUID PRIMARY KEY,
    designer_id UUID,
    note_text TEXT,
    FOREIGN KEY (designer_id) REFERENCES designers(designer_id)
);

CREATE TABLE users (
    user_id UUID PRIMARY KEY,
    username VARCHAR(50) UNIQUE NOT NULL,
    password VARCHAR(100) NOT NULL
);
