tables.table_schema:
CREATE TABLE DesignSketches (
    sketch_id UUID PRIMARY KEY,
    designer_name VARCHAR(50),
    sketch_image BYTEA
);

CREATE TABLE InspirationImages (
    image_id UUID PRIMARY KEY,
    designer_name VARCHAR(50),
    image_url TEXT
);

CREATE TABLE FabricSwatches (
    swatch_id UUID PRIMARY KEY,
    designer_name VARCHAR(50),
    fabric_type VARCHAR(50),
    swatch_image BYTEA
);

CREATE TABLE DesignSketches (
  sketch_id SERIAL PRIMARY KEY,
  designer_name VARCHAR(50) NOT NULL,
  sketch_description TEXT,
  sketch_image BYTEA
);

CREATE TABLE InspirationImages (
  image_id SERIAL PRIMARY KEY,
  designer_name VARCHAR(50) NOT NULL,
  image_description TEXT,
  image_data BYTEA
);

CREATE TABLE Designers (
  designer_id SERIAL PRIMARY KEY,
  designer_name VARCHAR(50) UNIQUE NOT NULL,
  designer_email VARCHAR(100) UNIQUE,
  designer_phone VARCHAR(20)
);

CREATE TABLE sketch_tags (
    tag_id INT PRIMARY KEY,
    sketch_id INT,
    tag_name VARCHAR(50)
);

CREATE TABLE sketch_fabric (
    sketch_id INT,
    fabric_id INT,
    PRIMARY KEY(sketch_id, fabric_id)
);
