tables.table_schema:
CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name TEXT,
    email TEXT,
    age INT
);

CREATE TABLE product_recommendations (
  product_id INT,
  customer_id INT,
  recommendation_score FLOAT
);

CREATE TABLE customer_browsing_history (
  customer_id INT,
  product_id INT,
  browsing_time TIMESTAMP
);

CREATE TABLE customer_purchases (
  order_id INT,
  customer_id INT,
  product_id INT,
  purchase_date DATE
);
