tables.table_schema:
CREATE TABLE sustainability_practices (
    id INT PRIMARY KEY,
    brand_name VARCHAR(255),
    eco_friendly BOOLEAN,
    fair_trade BOOLEAN
);

CREATE TABLE ethical_sourcing (
    id INT PRIMARY KEY,
    brand_name VARCHAR(255),
    certification VARCHAR(50),
    supplier_name VARCHAR(255)
);

CREATE TABLE production_methods (
    id INT PRIMARY KEY,
    brand_name VARCHAR(255),
    sustainable_materials BOOLEAN,
    energy_efficient BOOLEAN
);

CREATE TABLE sustainability_practices (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    sustainability_rating INT,
    certification_status BOOLEAN
);

CREATE TABLE ethical_sourcing (
    id INT PRIMARY KEY,
    product_name VARCHAR(255),
    supplier_name VARCHAR(255),
    ethical_rating INT
);

CREATE TABLE production_methods (
    id INT PRIMARY KEY,
    method_name VARCHAR(255),
    description TEXT,
    labor_conditions_rating INT
);

CREATE TABLE transparency (
    id INT PRIMARY KEY,
    company_name VARCHAR(255),
    transparency_rating INT,
    certification_status BOOLEAN
);

CREATE TABLE Sustainability_Practices (
    id SERIAL PRIMARY KEY,
    brand_name VARCHAR(100) NOT NULL,
    sustainability_rating INT,
    practices TEXT
);

CREATE TABLE Production_Methods (
    id SERIAL PRIMARY KEY,
    method_name VARCHAR(100) NOT NULL,
    efficiency_rating INT,
    environmental_impact TEXT
);
