tables.table_schema:
CREATE TABLE sensitive_data (
    id INT PRIMARY KEY,
    email VARCHAR(255),
    password VARCHAR(255),
    encryption_key VARCHAR(50)
);

CREATE TABLE access_roles (
    role_id INT PRIMARY KEY,
    role_name VARCHAR(50),
    permission_level VARCHAR(50)
);

CREATE TABLE users (
  id INT PRIMARY KEY,
  username VARCHAR(50) NOT NULL,
  password VARCHAR(100) NOT NULL
);

CREATE TABLE sensitive_data (
  data_id INT PRIMARY KEY,
  user_id INT REFERENCES users(id),
  encrypted_data VARCHAR(200) NOT NULL
);

CREATE TABLE access_controls (
  access_id INT PRIMARY KEY,
  user_id INT REFERENCES users(id),
  permission_level VARCHAR(20) NOT NULL
);

CREATE TABLE sensitive_data(
    id INT PRIMARY KEY,
    name VARCHAR(50),
    ssn VARCHAR(11) ENCRYPTED,
    salary DECIMAL(10, 2) ENCRYPTED
);

CREATE TABLE access_controls(
    id INT PRIMARY KEY,
    user_id INT,
    role VARCHAR(50) ENCRYPTED,
    access_level INT
);

CREATE TABLE audit_logs(
    id INT PRIMARY KEY,
    action_type VARCHAR(50),
    action_time TIMESTAMP,
    user_id INT
);

CREATE TABLE encryption_keys(
    key_id INT PRIMARY KEY,
    key_name VARCHAR(50),
    key_value VARCHAR(100) ENCRYPTED
);

CREATE TABLE Employee (
    employee_id INT PRIMARY KEY,
    name TEXT NOT NULL,
    email TEXT UNIQUE,
    password TEXT NOT NULL
);

CREATE TABLE Department (
    department_id INT PRIMARY KEY,
    name TEXT NOT NULL,
    manager_id INT,
    CONSTRAINT fk_manager FOREIGN KEY(manager_id) REFERENCES Employee(employee_id)
);

CREATE TABLE Salary (
    employee_id INT PRIMARY KEY,
    salary DECIMAL NOT NULL,
    bonus DECIMAL,
    CONSTRAINT fk_employee FOREIGN KEY(employee_id) REFERENCES Employee(employee_id)
);

CREATE TABLE EncryptedData (
    data_id INT PRIMARY KEY,
    encrypted_text TEXT NOT NULL,
    decryption_key TEXT NOT NULL,
    access_level INT CHECK (access_level IN (1, 2, 3))
);
