tables.table_schema:
CREATE TABLE pos_terminals (
    pos_terminal_id UUID PRIMARY KEY,
    terminal_location VARCHAR(255),
    terminal_status VARCHAR(50)
);

CREATE TABLE terminals (
    terminal_id SERIAL PRIMARY KEY,
    terminal_name VARCHAR(50),
    location VARCHAR(100),
    software_version VARCHAR(20)
);

CREATE TABLE customers (
    customer_id SERIAL PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(50),
    phone_number VARCHAR(15)
);

CREATE TABLE pos_terminal (
    id SERIAL PRIMARY KEY,
    terminal_name VARCHAR(50) NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE transaction (
    transaction_id SERIAL PRIMARY KEY,
    terminal_id INT NOT NULL,
    amount DECIMAL(10, 2) NOT NULL,
    transaction_date TIMESTAMP NOT NULL
);

CREATE TABLE user (
    user_id SERIAL PRIMARY KEY,
    username VARCHAR(50) NOT NULL,
    email VARCHAR(100) NOT NULL
);

CREATE TABLE synchronization (
    sync_id SERIAL PRIMARY KEY,
    last_sync_date TIMESTAMP NOT NULL,
    status VARCHAR(50) NOT NULL
);

CREATE TABLE pos_terminal (
    id INT PRIMARY KEY,
    terminal_name VARCHAR(50),
    location VARCHAR(100)
);

CREATE TABLE customer (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(100)
);

CREATE TABLE inventory (
    product_id INT PRIMARY KEY,
    product_name VARCHAR(50),
    price DECIMAL(10,2),
    quantity INT
);
