CREATE TABLE designer (
    designer_id UUID PRIMARY KEY,
    designer_name VARCHAR(100) NOT NULL,
    email VARCHAR(100) UNIQUE NOT NULL,
    location VARCHAR(100) NOT NULL
);

CREATE TABLE design_resource (
    resource_id UUID PRIMARY KEY,
    resource_name VARCHAR(100) NOT NULL,
    resource_description TEXT,
    designer_id UUID REFERENCES designer(designer_id)
);

CREATE TABLE designer (
  id INT PRIMARY KEY,
  name VARCHAR(50) NOT NULL,
  location VARCHAR(50),
  email VARCHAR(100) UNIQUE
);

CREATE TABLE design_ideas (
  idea_id INT PRIMARY KEY,
  designer_id INT,
  idea_description TEXT,
  CONSTRAINT fk_designer FOREIGN KEY (designer_id) REFERENCES designer(id)
);

CREATE TABLE design_resources (
  resource_id INT PRIMARY KEY,
  resource_name VARCHAR(100),
  category VARCHAR(50),
  url TEXT
);

CREATE TABLE designer (
  designer_id INT PRIMARY KEY,
  designer_name VARCHAR(50) NOT NULL,
  designer_email VARCHAR(50) UNIQUE NOT NULL,
  designer_location VARCHAR(50)
);

CREATE TABLE designer (
    designer_id SERIAL PRIMARY KEY,
    designer_name VARCHAR(50) NOT NULL,
    location VARCHAR(50) NOT NULL
);

CREATE TABLE design_idea (
    idea_id SERIAL PRIMARY KEY,
    idea_description TEXT NOT NULL,
    designer_id INT REFERENCES designer(designer_id) ON DELETE CASCADE
);

CREATE TABLE resource (
    resource_id SERIAL PRIMARY KEY,
    resource_name VARCHAR(50) NOT NULL,
    resource_type VARCHAR(50) NOT NULL,
    designer_id INT REFERENCES designer(designer_id) ON DELETE CASCADE
);

CREATE TABLE collaboration (
    collab_id SERIAL PRIMARY KEY,
    collab_description TEXT NOT NULL,
    designer1_id INT REFERENCES designer(designer_id) ON DELETE CASCADE,
    designer2_id INT REFERENCES designer(designer_id) ON DELETE CASCADE
);

CREATE TABLE event (
    event_id SERIAL PRIMARY KEY,
    event_name VARCHAR(50) NOT NULL,
    event_date DATE NOT NULL
);
