tables.table_schema:
CREATE TABLE fashion_brands (
    brand_id INT NOT NULL,
    brand_name VARCHAR(255) NOT NULL,
    consumer_segment VARCHAR(255) NOT NULL,
    PRIMARY KEY (brand_id)
);

CREATE TABLE brand (
    brand_id UUID PRIMARY KEY,
    brand_name VARCHAR(255) NOT NULL,
    established_year INT,
    location VARCHAR(255)
);

CREATE TABLE Brand (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR(255),
    brand_country VARCHAR(100),
    sustainable_level VARCHAR(50)
);

CREATE TABLE Brand (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR,
    headquarters_location VARCHAR,
    founding_date DATE
);

CREATE TABLE Sustainability (
    sustainability_id INT PRIMARY KEY,
    product_id INT,
    eco_friendly BOOLEAN,
    recycled_materials BOOLEAN
);
