tables.table_schema:
CREATE TABLE fashion_collections (
    collection_id INT PRIMARY KEY,
    collection_name VARCHAR(50),
    designer VARCHAR(50),
    release_date DATE
);

CREATE TABLE customers (
    customer_id INT PRIMARY KEY,
    customer_name VARCHAR(50),
    email VARCHAR(50),
    address VARCHAR(100)
);

CREATE TABLE fashion_brands (
  brand_id UUID PRIMARY KEY,
  brand_name VARCHAR NOT NULL,
  country VARCHAR NOT NULL,
  rating FLOAT
);

CREATE TABLE fashion_collections (
  collection_id UUID PRIMARY KEY,
  collection_title VARCHAR NOT NULL,
  brand_id UUID REFERENCES fashion_brands(brand_id),
  release_date DATE
);

CREATE TABLE fashion_products (
  product_id UUID PRIMARY KEY,
  product_name VARCHAR NOT NULL,
  collection_id UUID REFERENCES fashion_collections(collection_id),
  price DECIMAL
);

CREATE TABLE product_images (
  image_id UUID PRIMARY KEY,
  product_id UUID REFERENCES fashion_products(product_id),
  image_url VARCHAR NOT NULL
);

CREATE TABLE product (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    category VARCHAR(50),
    price DECIMAL(10, 2)
);

CREATE TABLE brand (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    headquarters VARCHAR(100),
    is_ecofriendly BOOLEAN
);

CREATE TABLE customer (
    id INT PRIMARY KEY,
    name VARCHAR(100),
    email VARCHAR(100),
    address VARCHAR(255)
);
