tables.table_schema:
CREATE TABLE design (
    design_id SERIAL PRIMARY KEY,
    design_name TEXT NOT NULL,
    designer_name TEXT NOT NULL
);

CREATE TABLE clothing_design (
    design_id SERIAL PRIMARY KEY,
    design_name TEXT NOT NULL,
    designer_name TEXT NOT NULL,
    design_date DATE
);

CREATE TABLE fabric_inventory (
    fabric_id SERIAL PRIMARY KEY,
    fabric_type TEXT NOT NULL,
    fabric_color TEXT,
    fabric_quantity INT NOT NULL
);

CREATE TABLE Design (
    design_id INT PRIMARY KEY,
    design_name VARCHAR(255),
    designer_name VARCHAR(255),
    design_description TEXT
);

CREATE TABLE Material (
    material_id INT PRIMARY KEY,
    material_name VARCHAR(255),
    material_type VARCHAR(50),
    quantity INT
);

CREATE TABLE Manufacturing (
    manufacturing_id INT PRIMARY KEY,
    production_id INT REFERENCES Production(production_id),
    manufacturing_date DATE,
    manufacturing_location VARCHAR(100)
);

