tables.table_schema:
CREATE TABLE fashion_photography_trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT,
    industry_preference_level INT
);

CREATE TABLE Trends (
  trend_id INT PRIMARY KEY,
  trend_name VARCHAR(50) NOT NULL,
  trend_description TEXT
);

CREATE TABLE Styles (
  style_id INT PRIMARY KEY,
  style_name VARCHAR(50) NOT NULL,
  popularity_score FLOAT
);

CREATE TABLE fashion_styles (
    style_id INT PRIMARY KEY,
    style_name VARCHAR(50) NOT NULL,
    average_likes INT,
    designer VARCHAR(50)
);

CREATE TABLE photographers (
    photographer_id INT PRIMARY KEY,
    photographer_name VARCHAR(50) NOT NULL,
    experience_years INT,
    nationality VARCHAR(50)
);

CREATE TABLE fashion_shoots (
    shoot_id INT PRIMARY KEY,
    shoot_date DATE NOT NULL,
    location VARCHAR(100),
    photographer_id INT REFERENCES photographers(photographer_id)
);

CREATE TABLE shoot_styles (
    shoot_id INT REFERENCES fashion_shoots(shoot_id),
    style_id INT REFERENCES fashion_styles(style_id),
    PRIMARY KEY(shoot_id, style_id)
);
