tables.table_schema:
CREATE TABLE Images (
  id INT PRIMARY KEY,
  image_url VARCHAR(255) NOT NULL,
  description TEXT
);

CREATE TABLE trends_images(
    image_id UUID PRIMARY KEY,
    image_url TEXT,
    description TEXT
);

CREATE TABLE fashion_trends(
    trend_id SERIAL PRIMARY KEY,
    trend_name VARCHAR(50) NOT NULL,
    trend_description TEXT
);

CREATE TABLE inspiration_boards(
    board_id SERIAL PRIMARY KEY,
    board_name VARCHAR(50) NOT NULL,
    trend_id INT REFERENCES fashion_trends(trend_id)
);

CREATE TABLE trends (
    id INT PRIMARY KEY,
    image_url VARCHAR(255),
    description TEXT
);

CREATE TABLE editorial_content (
