tables.table_schema:
CREATE TABLE IF NOT EXISTS industry_news (
  news_id UUID PRIMARY KEY,
  news_title TEXT,
  news_date DATE,
  news_content TEXT
);

CREATE TABLE industry_news (
  news_id INT PRIMARY KEY,
  news_title VARCHAR(255),
  news_description TEXT,
  news_date DATE
);

CREATE TABLE Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50),
    event_date DATE,
    event_location VARCHAR(100)
);

CREATE TABLE RunwayShows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(50),
    show_date DATE,
    show_location VARCHAR(100)
);

CREATE TABLE IndustryNews (
    news_id INT PRIMARY KEY,
    news_title VARCHAR(100),
    news_description TEXT,
    news_date DATE
);

CREATE TABLE EditorialCoverage (
    coverage_id INT PRIMARY KEY,
    coverage_title VARCHAR(100),
    coverage_date DATE,
    coverage_author VARCHAR(50)
);

CREATE TABLE EventPromotion (
    promotion_id INT PRIMARY KEY,
    promotion_title VARCHAR(100),
    promotion_date DATE,
    promotion_channel VARCHAR(50)
);
