tables.table_schema:
CREATE TABLE vip_guests (
   guest_id INT PRIMARY KEY,
   guest_name VARCHAR(50) NOT NULL,
   seating_position VARCHAR(10) NOT NULL
);

CREATE TABLE Seating_Arrangements (
    seating_table_id INT PRIMARY KEY,
    table_description VARCHAR(50),
    table_capacity INT,
    table_location VARCHAR(50)
);

CREATE TABLE guests (
    guest_id INT PRIMARY KEY,
    guest_name VARCHAR(50) NOT NULL,
    guest_arrival_time TIMESTAMP NOT NULL,
    seat_number INT NOT NULL
);

CREATE TABLE seating_arrangements (
    seat_id INT PRIMARY KEY,
    seat_number INT NOT NULL,
    is_occupied BOOLEAN DEFAULT false
);

CREATE TABLE fashion_shows (
    show_id INT PRIMARY KEY,
    show_date DATE NOT NULL,
    show_location VARCHAR(100) NOT NULL
);

CREATE TABLE attendance (
    attendance_id INT PRIMARY KEY,
    guest_id INT NOT NULL,
    show_id INT NOT NULL,
    attendance_status VARCHAR(10) NOT NULL
);

CREATE TABLE Guests (
    guest_id INT PRIMARY KEY,
    guest_name VARCHAR(50) NOT NULL,
    guest_email VARCHAR(50) NOT NULL,
    guest_type VARCHAR(20) NOT NULL
);

CREATE TABLE Events (
    event_id INT PRIMARY KEY,
    event_name VARCHAR(50) NOT NULL,
    event_date DATE NOT NULL,
    event_location VARCHAR(100) NOT NULL
);

CREATE TABLE Ratings (
    rating_id INT PRIMARY KEY,
    event_id INT REFERENCES Events(event_id),
    guest_id INT REFERENCES Guests(guest_id),
    rating INT NOT NULL
);
