tables.table_schema:
CREATE TABLE fashion_collections (
    collection_id INT PRIMARY KEY,
    collection_name VARCHAR(255),
    designer_name VARCHAR(255),
    year INT
);

CREATE TABLE SalesData (
    sale_id INT PRIMARY KEY,
    collection_name VARCHAR(50),
    sale_date DATE,
    sale_amount DECIMAL(10, 2)
);

CREATE TABLE FashionDesigners (
    designer_id INT PRIMARY KEY,
    designer_name VARCHAR(50),
    designer_specialization VARCHAR(50),
    designer_experience INT
);

CREATE TABLE FashionTrends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_description TEXT,
    trend_season VARCHAR(10)
);

CREATE TABLE FashionShows (
    show_id UUID PRIMARY KEY,
    show_date TIMESTAMP,
    location VARCHAR(100)
);

CREATE TABLE Collection (
    collection_id UUID PRIMARY KEY,
    collection_name TEXT NOT NULL,
    designer_name TEXT NOT NULL,
    launch_date DATE
);

CREATE TABLE ClothingItem (
    item_id SERIAL PRIMARY KEY,
    item_name TEXT NOT NULL,
    collection_id UUID REFERENCES Collection(collection_id),
    category TEXT NOT NULL,
    price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE SalesRecord (
    record_id SERIAL PRIMARY KEY,
    item_id INTEGER REFERENCES ClothingItem(item_id),
    sale_date DATE NOT NULL,
    quantity INTEGER NOT NULL,
    total_price DECIMAL(10, 2) NOT NULL
);

CREATE TABLE Customer (
    customer_id SERIAL PRIMARY KEY,
    customer_name TEXT NOT NULL,
    email TEXT UNIQUE
);

CREATE TABLE PurchaseRecord (
    purchase_id SERIAL PRIMARY KEY,
    customer_id INTEGER REFERENCES Customer(customer_id),
    item_id INTEGER REFERENCES ClothingItem(item_id),
    purchase_date DATE NOT NULL
);
