tables.table_schema:
CREATE TABLE designer_profiles (
    id INT PRIMARY KEY,
    name VARCHAR(255),
    age INT,
    location VARCHAR(255)
);

CREATE TABLE designer_portfolios (
    id INT PRIMARY KEY,
    designer_id INT,
    portfolio_name VARCHAR(255),
    creation_date DATE
);

CREATE TABLE DesignerShows (
    show_id INT REFERENCES FashionShows(show_id),
    designer_id INT REFERENCES DesignerProfiles(designer_id),
    show_outfit_id INT,
    outfit_description TEXT
);
