tables.table_schema:
CREATE TABLE Stylists (
    stylist_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    specialty VARCHAR(50),
    availability BOOLEAN
);

CREATE TABLE MakeupArtists (
    artist_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    experience_years INT,
    availability BOOLEAN
);

CREATE TABLE Dressers (
    dresser_id INT PRIMARY KEY,
    name VARCHAR(50) NOT NULL,
    role VARCHAR(50),
    availability BOOLEAN
);

CREATE TABLE HairStylists (
    stylist_id INT PRIMARY KEY,
    stylist_name VARCHAR(50) NOT NULL,
    stylist_contact VARCHAR(50) NOT NULL
);

CREATE TABLE MakeupArtists (
    artist_id INT PRIMARY KEY,
    artist_name VARCHAR(50) NOT NULL,
    artist_contact VARCHAR(50) NOT NULL
);

CREATE TABLE Dressers (
    dresser_id INT PRIMARY KEY,
    dresser_name VARCHAR(50) NOT NULL,
    dresser_contact VARCHAR(50) NOT NULL
);

CREATE TABLE FashionShows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(50) NOT NULL,
    show_location VARCHAR(100) NOT NULL,
    show_date DATE NOT NULL
);
