tables.table_schema:
CREATE TABLE FashionShowEvents (
    event_id UUID PRIMARY KEY,
    event_name VARCHAR(100) NOT NULL,
    event_date TIMESTAMP NOT NULL,
    event_location VARCHAR(100) NOT NULL
);

CREATE TABLE Guests (
    guest_id UUID PRIMARY KEY,
    guest_name VARCHAR(100) NOT NULL,
    guest_email VARCHAR(100) NOT NULL,
    guest_phone VARCHAR(20) 
);

CREATE TABLE Invitations (
    invitation_id UUID PRIMARY KEY,
    event_id UUID REFERENCES FashionShowEvents(event_id),
    guest_id UUID REFERENCES Guests(guest_id),
    invitation_status VARCHAR(20) DEFAULT 'Pending'
);

CREATE TABLE GuestPreferences (
    preference_id UUID PRIMARY KEY,
    guest_id UUID REFERENCES Guests(guest_id),
    seating_preference VARCHAR(50),
    dietary_restrictions VARCHAR(100)
);

CREATE TABLE Invitation (
  invitation_id INT PRIMARY KEY,
  attendee_name VARCHAR(50),
  email VARCHAR(100),
  event_type VARCHAR(50)
);

CREATE TABLE FashionShow (
  show_id INT PRIMARY KEY,
  show_name VARCHAR(50),
  show_date DATE,
  location VARCHAR(100)
);

CREATE TABLE AttendeePreferences (
  attendee_id INT PRIMARY KEY,
  attendee_name VARCHAR(50),
  fashion_style VARCHAR(50),
  favorite_designer VARCHAR(50)
);

CREATE TABLE RSVP (
  rsvp_id INT PRIMARY KEY,
  invitation_id INT,
  response VARCHAR(10),
  FOREIGN KEY (invitation_id) REFERENCES Invitation(invitation_id)
);
