tables.table_schema:
CREATE TABLE Sponsorship_Agreements (
    agreement_id INT PRIMARY KEY,
    sponsor_name VARCHAR(100),
    sponsorship_amount DECIMAL(10, 2),
    agreement_date DATE
);

CREATE TABLE Brand_Integrations (
    integration_id INT PRIMARY KEY,
    brand_name VARCHAR(100),
    integration_type VARCHAR(50),
    integration_date DATE
);

CREATE TABLE Fashion_Shows (
    show_id INT PRIMARY KEY,
    show_name VARCHAR(100),
    show_date DATE,
    location VARCHAR(100)
);

CREATE TABLE FashionShows (
	show_id INT PRIMARY KEY,
	show_name VARCHAR(255),
	show_date DATE,
	location VARCHAR(100)
);
