tables.table_schema:
CREATE TABLE PressFeedback (
    feedback_id SERIAL PRIMARY KEY,
    feedback_text TEXT,
    feedback_rating INT
);

CREATE TABLE AttendeeFeedback (
    feedback_id SERIAL PRIMARY KEY,
    attendee_id INT,
    feedback_text TEXT,
    feedback_rating INT
);

CREATE TABLE PressFeedback (
    feedback_id INT PRIMARY KEY,
    feedback_text TEXT,
    rating INT
);

CREATE TABLE AttendeeFeedback (
    feedback_id INT PRIMARY KEY,
    attendee_id INT,
    event_rating INT,
    comments TEXT
);

CREATE TABLE EventMetrics (
    metric_id INT PRIMARY KEY,
    event_id INT,
    attendees_count INT,
    overall_rating FLOAT
);

CREATE TABLE press_feedback (
  feedback_id SERIAL PRIMARY KEY,
  organization TEXT,
  feedback TEXT,
  rating INT
);

CREATE TABLE attendee_feedback (
  feedback_id SERIAL PRIMARY KEY,
  attendee_name TEXT,
  feedback TEXT,
  rating INT
);

CREATE TABLE event_stats (
  event_id SERIAL PRIMARY KEY,
  event_date DATE,
  total_attendees INT,
  total_press INT
);

CREATE TABLE press_contact (
  press_id SERIAL PRIMARY KEY,
  press_name TEXT,
  organization TEXT,
  contact_info TEXT
);

CREATE TABLE event_photos (
  photo_id SERIAL PRIMARY KEY,
  event_id INT,
  photo_url TEXT
);
