tables.table_schema:
CREATE TABLE celebrity_fashion_trends (
   celeb_id INT PRIMARY KEY,
   celeb_name VARCHAR(50),
   popular_color VARCHAR(20),
   popular_style VARCHAR(30)
);

CREATE TABLE popular_colors (
    color_id INT PRIMARY KEY,
    color_name VARCHAR(255)
);

CREATE TABLE popular_styles (
    style_id INT PRIMARY KEY,
    style_name VARCHAR(255)
);

CREATE TABLE celebrity_fashion (
    celebrity_id INT PRIMARY KEY,
    celebrity_name VARCHAR(255),
    color_id INT,
    style_id INT
);

CREATE TABLE celebrity (id INT PRIMARY KEY, name VARCHAR(255) NOT NULL, age INT, profession VARCHAR(255));

CREATE TABLE fashion_trends (id INT PRIMARY KEY, trend_name VARCHAR(255) NOT NULL, trend_type VARCHAR(255), popularity INT);

CREATE TABLE color_palette (id INT PRIMARY KEY, color_name VARCHAR(255) NOT NULL, hex_code VARCHAR(10), rgb_value VARCHAR(20));

CREATE TABLE style_guide (id INT PRIMARY KEY, style_name VARCHAR(255) NOT NULL, category VARCHAR(50), description TEXT);

CREATE TABLE colors (color_id INT PRIMARY KEY, color_name VARCHAR(255));
CREATE TABLE styles (style_id INT PRIMARY KEY, style_name VARCHAR(255));
CREATE TABLE celebrities (celebrity_id INT PRIMARY KEY, celebrity_name VARCHAR(255), celebrity_age INT);
CREATE TABLE outfits (outfit_id INT PRIMARY KEY, outfit_name VARCHAR(255), color_id INT, style_id INT);
CREATE TABLE celebrity_outfits (celebrity_id INT, outfit_id INT);
