tables.table_schema:
CREATE TABLE Trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    description TEXT
);

CREATE TABLE Events (
  id INT PRIMARY KEY,
  event_name VARCHAR(100),
  location VARCHAR(100),
  date DATE
);

CREATE TABLE brands (
    brand_id INT PRIMARY KEY,
    brand_name VARCHAR(50),
    country_origin VARCHAR(50)
);

CREATE TABLE trends (
    trend_id INT PRIMARY KEY,
    trend_name VARCHAR(50),
    trend_category VARCHAR(50),
    trend_description TEXT
);
