tables.table_schema:
CREATE TABLE red_carpet_events (
  event_id INT PRIMARY KEY,
  celebrity_name VARCHAR(50),
  event_date DATE,
  event_location VARCHAR(100)
);

CREATE TABLE red_carpet_appearances (
    event_id INT PRIMARY KEY,
    celebrity_name TEXT,
    event_date DATE,
    event_location TEXT
);

CREATE TABLE fashion_events (
    event_id INT PRIMARY KEY,
    event_name TEXT,
    event_date DATE,
    location TEXT
);

CREATE TABLE red_carpet_appearances (
    event_id INT PRIMARY KEY,
    celebrity_name VARCHAR(50) NOT NULL,
    event_date DATE NOT NULL,
    event_time TIME NOT NULL
);

CREATE TABLE fashion_designers (
    designer_id INT PRIMARY KEY,
    designer_name VARCHAR(50) NOT NULL,
    brand_name VARCHAR(50) NOT NULL
);

CREATE TABLE event_locations (
    location_id INT PRIMARY KEY,
    location_name VARCHAR(50) NOT NULL,
    city VARCHAR(50) NOT NULL,
    country VARCHAR(50) NOT NULL
);
