CREATE TABLE celebrity ( 
   celebrity_id INT PRIMARY KEY, 
   name VARCHAR(255), 
   age INT, 
   gender VARCHAR(10) 
 );

CREATE TABLE brand ( 
   brand_id INT PRIMARY KEY, 
   brand_name VARCHAR(255), 
   industry VARCHAR(100), 
   headquarters VARCHAR(255) 
 );

